mapper.select:
select attraction_no, content_id, content_type_id, area_code, si_gun_gu_code, title, first_image1, first_image2, map_level, latitude, longitude, tel, addr1, addr2, homepage, overview
from attractions
where si_gun_gu_code=#{siGunGuCode}
and area_code=#{areaCode}
order by RAND()

		select *
		from attractions
		where area_code=#{areaCode}
		and si_gun_gu_code=#{siGunGuCode}

       		and content_type_id IN

         #{type}
